pragma solidity ^0.5.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721Metadata.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721Mintable.sol";
import "@openzeppelin/contracts/drafts/Counters.sol";

contract ERC721MetadataString is ERC721Metadata, ERC721Mintable {
  using Counters for Counters.Counter;

  mapping (address => mapping (uint => bytes)) private entries;
  mapping (address => Counters.Counter) private counters;

  function addMetadata(string memory _cid)
  public
  {
    bytes memory cidAsBytes = bytes(_cid);
    entries[msg.sender][counters[msg.sender].current()] = cidAsBytes;

    counters[msg.sender].increment();
  }

  function getMetadata()
  public
  view
  returns(bytes memory _cid) {
    // require( entries[msg.sender][counters[msg.sender].current() - 1] != 0, "ERC721MetadataString.getMetadata.Empty data, reverting");

    return entries[msg.sender][counters[msg.sender].current() - 1];
  }
}
